mapper.select:
SELECT r.room_id, r.room_name, r.room_desc, r.room_addr, r.phone, r.room_img, r.priority, r.create_time, r.last_edit_time, r.enable_status, r.advice, a.area_id, a.area_name, rc.room_category_id, rc.room_category_name FROM tb_room r, tb_area a, tb_room_category rc
and r.room_category_id = #{roomCondition.roomCategory.roomCategoryId}
and r.area_id = #{roomCondition.area.areaId}
and r.room_name like '%${roomCondition.roomName}%'
and r.enable_status = #{roomCondition.enableStatus}
and r.owner_id = #{roomCondition.owner.userId}
AND r.area_id = a.area_id AND r.room_category_id = rc.room_category_id
ORDER BY r.priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_room r, tb_area a, tb_room_category rc
and r.room_category_id = #{roomCondition.roomCategory.roomCategoryId}
and r.area_id = #{roomCondition.area.areaId}
and r.room_name like '%${roomCondition.roomName}%'
and r.enable_status = #{roomCondition.enableStatus}
and r.owner_id = #{roomCondition.owner.userId}
AND r.area_id = a.area_id AND r.room_category_id = rc.room_category_id

mapper.update:
update tb_room
room_name=#{roomName}, room_desc=#{roomDesc}, room_addr=#{roomAddr}, phone=#{phone}, room_img=#{roomImg}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, room_category_id=#{roomCategory.roomCategoryId}
where room_id=#{roomId}
